// @google/genai-codex-fix: Zastąpiono niekompletną funkcję 'playAudio' pełną implementacją i dodano brakujący eksport 'SOUNDS'.
// Sound effects utility
export const playAudio = (soundUrl: string, volume: number = 0.5) => {
  try {
    const audio = new Audio(soundUrl);
    audio.volume = volume;
    const playPromise = audio.play();

    if (playPromise !== undefined) {
      playPromise.catch((error: DOMException) => {
        // Autoplay was prevented. This is a common browser policy.
        if (error.name === 'NotAllowedError') {
          // This is not a critical error, just a browser restriction.
          console.warn(`Odtwarzanie audio ("${soundUrl.substring(0, 30)}...") zostało zablokowane przez przeglądarkę. Wymagana jest interakcja użytkownika, aby aktywować dźwięk.`);
        } else if (error.name === 'AbortError') {
          // Playback was aborted. This can happen if another action interrupts playback (e.g., rapid clicks).
          // This is usually not a critical error.
          console.log(`Odtwarzanie audio ("${soundUrl.substring(0, 30)}...") zostało przerwane (prawdopodobnie przez kolejne odtworzenie).`);
        } else {
          // For other errors, log them as errors with more detail.
          console.error(`Błąd odtwarzania audio dla "${soundUrl.substring(0, 30)}...":`, error);
        }
      });
    }
  } catch (error) {
    console.error(`Błąd tworzenia elementu Audio dla "${soundUrl.substring(0, 30)}...":`, error);
  }
};

